// Vercel Serverless Function - Gemini 2.0 Flash Lite + 80개 백업 시스템
export default async function handler(req, res) {
  res.setHeader('Access-Control-Allow-Credentials', true);
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Methods', 'GET,OPTIONS,PATCH,DELETE,POST,PUT');
  res.setHeader('Access-Control-Allow-Headers', 'X-CSRF-Token, X-Requested-With, Accept, Accept-Version, Content-Length, Content-MD5, Content-Type, Date, X-Api-Version');

  if (req.method === 'OPTIONS') {
    res.status(200).end();
    return;
  }

  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const { situation } = req.body;
    if (!situation) return res.status(400).json({ error: 'Situation is required' });

    const apiKey = process.env.GEMINI_API_KEY;
    if (!apiKey) {
      console.log('No API key - using backup');
      return res.status(200).json(getBackupVerse(situation));
    }

    try {
      const response = await fetch(
        `https://generativelanguage.googleapis.com/v1/models/gemini-2.0-flash-lite:generateContent?key=${apiKey}`,
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            contents: [{ parts: [{ text: `당신은 성경에 정통한 목회 상담가입니다. 사용자의 상황에 맞는 성경 구절을 찾아주고, 그 의미를 자세히 설명해주세요.

사용자가 "${situation}" 상황에 있습니다.

이 상황에 가장 적합한 성경 구절 하나를 찾아주세요. 매번 다른 구절을 추천해주세요.

다음 정보를 JSON 형식으로만 제공해주세요:

{
  "reference": "책 장:절",
  "text": "성경 구절 원문 (한글)",
  "context": "이 구절의 성경적 문맥과 배경 설명 (3-4문장)",
  "meaning": "현재 상황에 대한 적용 (3-4문장)",
  "prayer": "말씀을 바탕으로 한 기도문 (2-3문장)"
}

JSON만 응답하세요.` }] }],
            generationConfig: { temperature: 0.8, topK: 40, topP: 0.95, maxOutputTokens: 2048 }
          })
        }
      );

      if (!response.ok) {
        console.log(`API error ${response.status} - using backup`);
        return res.status(200).json(getBackupVerse(situation));
      }

      const data = await response.json();
      if (data.candidates && data.candidates[0]?.content?.parts?.[0]?.text) {
        const content = data.candidates[0].content.parts[0].text;
        try {
          const cleanContent = content.replace(/```json\n?|\n?```/g, '').trim();
          return res.status(200).json(JSON.parse(cleanContent));
        } catch (e) {
          const jsonMatch = content.match(/\{[\s\S]*\}/);
          if (jsonMatch) {
            try { return res.status(200).json(JSON.parse(jsonMatch[0])); }
            catch (e2) { return res.status(200).json(getBackupVerse(situation)); }
          }
        }
      }
      return res.status(200).json(getBackupVerse(situation));
    } catch (apiError) {
      console.error('API call failed:', apiError);
      return res.status(200).json(getBackupVerse(situation));
    }
  } catch (error) {
    console.error('Server error:', error);
    return res.status(200).json(getBackupVerse(req.body.situation || 'comfort'));
  }
}

