function getBackupVerse(situation) {
  let category = findCategory(situation);
  const verses = BACKUP_VERSES[category] || BACKUP_VERSES['위로가 필요할 때'];
  const randomIndex = Math.floor(Math.random() * verses.length);
  return verses[randomIndex];
}

function findCategory(situation) {
  const categoryMap = {
    '위로가 필요할 때': ['위로', 'comfort', '힘들', '어려', '슬플', '아플'],
    '불안하고 두려울 때': ['불안', '두려', 'anxiety', '걱정', '염려', '무서'],
    '감사할 때': ['감사', 'gratitude', '고마', '감사드'],
    '결정이 필요할 때': ['결정', 'decision', '선택', '고민', '결단'],
    '힘이 필요할 때': ['힘', 'strength', '강함', '능력', '지쳐', '피곤'],
    '용서하고 싶을 때': ['용서', 'forgiveness', '화해'],
    '희망이 필요할 때': ['희망', 'hope', '소망', '미래'],
    '사랑에 대해 알고 싶을 때': ['사랑', 'love']
  };
  
  for (const [category, keywords] of Object.entries(categoryMap)) {
    if (keywords.some(keyword => situation.includes(keyword))) {
      return category;
    }
  }
  
  return '위로가 필요할 때';
}
